package org.usfirst.frc5053.FRC2016Stronghold.commands;

import edu.wpi.first.wpilibj.command.Command;
import edu.wpi.first.wpilibj.smartdashboard.SmartDashboard;
import edu.wpi.first.wpilibj.PIDSource;

import org.usfirst.frc5053.FRC2016Stronghold.Robot;
import org.usfirst.frc5053.FRC2016Stronghold.RobotMap;
import org.usfirst.frc5053.FRC2016Stronghold.MotionControlHelper;
import org.usfirst.frc5053.FRC2016Stronghold.MotionControlPIDController;
import org.usfirst.frc5053.FRC2016Stronghold.RobotDriveSpinPIDOutput;

/**
 *
 */
public class DriveSpin extends Command {
	double turn = -180;//default in degrees
	double targetAngle = 90; // temp real value calculated below
	double targetTolerance = 1 ; //degrees

    double     ramp =  30; //degrees
    double maxspeed = 15.0*(360/60) ; //360/60 converts the first numbers which is in RPM to degrees/second
    double    start =   0; //degrees
    MotionControlHelper rotationSpeedProfile; 
    
    final double Kp = 1d/(60*(360/6)); // so at denominator off in the spin-Rate the power will reach the max
    final double Ki = 0.0005;
    final double Kd = 0.0;
    MotionControlPIDController rotationSpeedPID;
    
    public DriveSpin(double a_turn) {
        SmartDashboard.putString("DriveSpinCodeLocation","entered DriveSpin(a_turn)");
		   turn = a_turn;
	       // Use requires() here to declare subsystem dependencies
	       // eg. requires(chassis);

	       // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=REQUIRES
        requires(Robot.driveTrain);

    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=REQUIRES
         
	 }

    // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=VARIABLE_DECLARATIONS
 
    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=VARIABLE_DECLARATIONS

    // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=CONSTRUCTOR
    public DriveSpin() {
        SmartDashboard.putString("DriveSpinCodeLocation","entered DriveSpin()");

    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=CONSTRUCTOR
        // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=VARIABLE_SETTING

        // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=VARIABLE_SETTING
        // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=REQUIRES
        requires(Robot.driveTrain);

    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=REQUIRES
        //Setup the rotation profile, how fast we are going to spin & the rampUp/RampDown distance to get there
    }

    // Called just before this Command runs the first time
    protected void initialize() {
        SmartDashboard.putString("DriveSpinCodeLocation","entered initialize()");
//    	   	RobotMap.gyroToUse.reset();
    	   	
 //   	    rotationSpeedPID.setAbsoluteTolerance(targetTolerance);
//    	    mcPID.free();
        start = RobotMap.gyroToUse.getAngle();
		targetAngle = turn + start;
        rotationSpeedProfile = new MotionControlHelper(targetAngle, ramp, maxspeed, start, (PIDSource) RobotMap.gyroToUse,new RobotDriveSpinPIDOutput());
        rotationSpeedPID = new MotionControlPIDController(Kp,Ki,Kd, rotationSpeedProfile );
    	    rotationSpeedPID.setOutputRange(-1.0, 1.0);
    	    rotationSpeedPID.enable();
    }

    // Called repeatedly when this Command is scheduled to run
    protected void execute() {
        SmartDashboard.putString("DriveSpinCodeLocation","entered execute()");
        System.out.println("DriveSpinCodeLocation "+"entered execute()");
    	// get current Angle to report on
    	double currentAngle = RobotMap.gyroToUse.getAngle();
        SmartDashboard.putNumber("DriveSpin Current angle",currentAngle);
        SmartDashboard.putNumber("DriveSpin Turn",turn);
        SmartDashboard.putNumber("DriveSpin Gyro Rate",RobotMap.gyroToUse.getRate());
        SmartDashboard.putNumber("DriveSpin Gryro Target Rate",rotationSpeedProfile.getTargetSpeed(currentAngle));
        SmartDashboard.putNumber("DriveSpin Time",this.timeSinceInitialized());
    }

    // Make this return true when this Command no longer needs to run execute()
    protected boolean isFinished() {
        SmartDashboard.putString("DriveSpinCodeLocation","entered isFinished()");
        
    	if(Math.abs(RobotMap.gyroToUse.getAngle()-targetAngle)<targetTolerance) { 
       		rotationSpeedPID.disable();
//       		mcPID.getError() 
       		System.out.println("DriveSpin Finished true");  
       	  	RobotMap.driveTrainRobotDrive21.tankDrive(0,0);
       		return  true;
       	}
       	else{
               return false;
       	}

    }

    // Called once after isFinished returns true
    protected void end() {
        SmartDashboard.putString("DriveSpinCodeLocation","entered end()");
        System.out.println("DriveSpinCodeLocation "+"entered end()");
   	rotationSpeedPID.disable();
    }

    // Called when another command which requires one or more of the same
    // subsystems is scheduled to run
    protected void interrupted() {
        SmartDashboard.putString("DriveSpinCodeLocation","entered interrupted()");
        System.out.println("DriveSpinCodeLocation "+"entered end()");
        end();
    }
}
